import React, { useState, useEffect } from 'react';
import { FlatList, StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import firebase from "./firebase";
import { useNavigation } from '@react-navigation/native';



const DocumentListScreen = () => {
  const [documents, setDocuments] = useState([]);
  
  const navigation = useNavigation();

  useEffect(() => {
    const fetchDocuments = async () => {
      const response = await firebase.db.collection('datos').get();
      const data = response.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
      setDocuments(data);
      //console.log(data)
    };

    fetchDocuments();
  }, []);

  const handleDocumentPress = (documents) => {
    navigation.navigate('DocumentDetailsScreen', { documents });
  };

  return (
    <View style={styles.container}>
      <FlatList
        data={documents}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => (
          <TouchableOpacity style={styles.document} onPress={() => handleDocumentPress(item)}>
            <Text>{item.edad}</Text>
          </TouchableOpacity>
        )}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  document: {
    padding: 16,
    borderBottomWidth: 1,
    borderBottomColor: '#ccc',
  },
});

export default DocumentListScreen;
