import React from 'react';
//desde aqui
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Button, TextInput } from 'react-native';
import { useState } from 'react';
import { printToFileAsync } from 'expo-print';
import { shareAsync } from 'expo-sharing';




const DocumentDetailsScreen = ({ route }) => {
  const { documents } = route.params;
  //desde aqui
  let [name, setName] = useState("");

  const html = `
    <html>
    <head>
	<title>Dos licencias de conducir</title>
	<style>
  .image-container {
    display: flex;
    justify-content: space-between;
  }
  
  .image-wrapper {
    position: relative;
    width: 100%;
    left: 10%;
  }
		.imagen {
			border-radius: 10px;
			max-width: 50px;
      transform: rotate(-90deg);
		}
    .rectangle {
      position: absolute;
      top: 5%;
      left: 10%;
      width: 231px;
      height: 210px;
      border: 4px solid red;
      border-radius: 20px;
      box-sizing: border-box;
      transform: rotate(-90deg);
    }
    .image-wrapper img {
      max-width: 70%;
      max-height: 70%;
      border-radius: 10px;
      object-fit: cover;
    }

	</style>
</head>
      <body>
        <h1>Hi ${name}</h1>
        <p style="color: red;">Hello. Bonjour. Hola.</p>
        <div class="image-container">
        <div class="image-wrapper">
          <img class="imagen" src=${documents.foto1} alt="Licencia 1">
          <div class="rectangle"></div>
        </div>
        <div class="image-wrapper">
          <img class="imagen" src=${documents.foto2} alt="Licencia 2">
          <div class="rectangle"></div>
        </div>
      </div>
      </body>
    </html>
  `;

  let generatePdf = async () => {
    const file = await printToFileAsync({
      html: html,
      base64: false
    });

    await shareAsync(file.uri);
  };
  //hasta aqui

  return (
    <View style={styles.container}>

       <TextInput value={name} placeholder="Name" style={styles.textInput} onChangeText={(value) => setName(value)} />
      <Button title="Generate PDF" onPress={generatePdf} />
      <StatusBar style="auto" />

      <Text>{documents.id}</Text>
      <Text>{documents.edad}</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  textInput: {
    alignSelf: "stretch",
    padding: 8,
    margin: 8
  }
});

export default DocumentDetailsScreen;
